# Script to get the geolocations of the addresses of the 2nd zone into a JSON file
echo '[' >geolocations.json

for street in 'CALLE%20BERNARDO%20MARTIN%20PEREZ%20SALAMANCA%20SALAMANCA' 'CALLE%20DIMAS%20MARADIAGA%20SALAMANCA%20SALAMANCA' 'CALLE%20DON%20BOSCO%20SALAMANCA%20SALAMANCA' 'CALLE%20DON%20JOSE%20ARTERO%20SALAMANCA%20SALAMANCA' 'CALLE%20JUAN%20DEL%20ENCINA%20SALAMANCA%20SALAMANCA' 'CALLE%20LAS%20ERAS%20SALAMANCA%20SALAMANCA' 'CALLE%20MALDONADO%20OCAMPO%20SALAMANCA%20SALAMANCA' 'CALLE%20PEREZ%20OLIVA%20SALAMANCA%20SALAMANCA' 'CALLE%20POLLO%20MARTÍN%20SALAMANCA%20SALAMANCA' 'CALLE%20RODRIGUEZ%20FABRES%20SALAMANCA%20SALAMANCA' 'CALLE%20SANCHEZ%20LLEVOT%20SALAMANCA%20SALAMANCA' 'CALLE%20SANTANDER%20SALAMANCA%20SALAMANCA' 'CALLE%20AGUSTIN%20DEL%20CAÑIZO%20SALAMANCA%20SALAMANCA' 'CALLE%20ANTONIO%20ESPINOSA%20SALAMANCA%20SALAMANCA' 'CALLE%20ASTURIAS%20SALAMANCA%20SALAMANCA' 'CALLE%20ESCULTOR%20MARTINEZ%20MONTAÑES%20SALAMANCA%20SALAMANCA' 'CALLE%20FRAY%20LUIS%20DE%20GRANADA%20SALAMANCA%20SALAMANCA' 'CALLE%20ISIDRO%20SEGOVIA%20SALAMANCA%20SALAMANCA' 'CALLE%20JOAQUIN%20COSTA%20SALAMANCA%20SALAMANCA' 'CALLE%20JUAN%20DE%20JUNI%20SALAMANCA%20SALAMANCA' 'CALLE%20LOS%20CHURRIGUERA%20SALAMANCA%20SALAMANCA' 'CALLE%20MELCHOR%20CANO%20SALAMANCA%20SALAMANCA' 'CALLE%20NIETO%20BONAL%20SALAMANCA%20SALAMANCA' 'CALLE%20PADRE%20BAÑEZ%20SALAMANCA%20SALAMANCA' 'CALLE%20PADRE%20MANJON%20SALAMANCA%20SALAMANCA' 'CALLE%20PAPIN%20SALAMANCA%20SALAMANCA' 'CALLE%20PINTOR%20MORALES%20SALAMANCA%20SALAMANCA' 'CALLE%20PROFESOR%20SAEZ%20SALAMANCA%20SALAMANCA' 'AVENIDA%20ALFONSO%20IX%20DE%20LEON%20100%20SALAMANCA%20SALAMANCA' 'AVENIDA%20ALFONSO%20VI%20SALAMANCA%20SALAMANCA' 'CALLE%20ALONSO%20DE%20OJEDA%20SALAMANCA%20SALAMANCA' 'CALLE%20ARROYO%20DE%20LA%20LASTRA%20SALAMANCA%20SALAMANCA' 'CALLE%20CONDE%20CABARRUS%20SALAMANCA%20SALAMANCA' 'CALLE%20CONDE%20DON%20VELA%20SALAMANCA%20SALAMANCA' 'CALLE%20FERNANDO%20DE%20LA%20PEÑA%20SALAMANCA%20SALAMANCA' 'CALLE%20FERNANDO%20DE%20ROJAS%20SALAMANCA%20SALAMANCA' 'CALLE%20LOS%20HIDALGOS%20SALAMANCA%20SALAMANCA' 'PLAZA%20MADRID%20SALAMANCA%20SALAMANCA' 'CALLE%20NORTE%20SALAMANCA%20SALAMANCA' 'CALLE%20PEREZ%20HERRASTI%20SALAMANCA%20SALAMANCA' 'CALLE%20PINZONES%20SALAMANCA%20SALAMANCA' 'CALLE%20PIZARRO%20SALAMANCA%20SALAMANCA' 'CALLE%20SARASATE%20SALAMANCA%20SALAMANCA' 'CALLE%20SUR%20SALAMANCA%20SALAMANCA' 'CALLE%20TOLEDO%20SALAMANCA%20SALAMANCA' 'CALLE%20VELAZQUEZ%20SALAMANCA%20SALAMANCA'; do
    echo $street
    echo "https://nominatim.openstreetmap.org/search?format=json&limit=5&q=$street"
    curl "https://nominatim.openstreetmap.org/search?format=json&limit=5&q=$street" \
        -H 'authority: nominatim.openstreetmap.org' \
        -H 'accept: */*' \
        -H 'accept-language: es,es-ES;q=0.9,en;q=0.8,en-GB;q=0.7,en-US;q=0.6' \
        -H 'origin: null' \
        -H 'sec-fetch-dest: empty' \
        -H 'sec-fetch-mode: cors' \
        -H 'sec-fetch-site: cross-site' \
        -H 'user-agent: Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/108.0.0.0 Safari/537.36 Edg/108.0.1462.54' \
        --compressed  >> geolocations.json
    echo ',' >> geolocations.json
    sleep 2
done

echo ']' >> geolocations.json